changeSet.sql:
DROP TABLE IF EXISTS document_types;

CREATE TABLE document_types
(
id           BigSerial      PRIMARY KEY,
name         VARCHAR (255)  not null,
descriptions TEXT,
created_at   TIMESTAMP,
updated_at   TIMESTAMP
);

DROP TABLE IF EXISTS attributes;

CREATE TABLE attributes
(
id           BigSerial      PRIMARY KEY,
name         VARCHAR (255)  not null,
data_type    VARCHAR (255)  not null
);

DROP TABLE IF EXISTS document_type_attributes;

CREATE TABLE document_type_attributes
(
doc_type_id     bigint,
attribute_id    bigint,
CONSTRAINT doc_type_id_foreign_key FOREIGN KEY (doc_type_id) REFERENCES document_types (id),
CONSTRAINT attribute_id_foreign_key FOREIGN KEY (attribute_id) REFERENCES attributes(id)
);
