changeSet.sql:
CREATE TABLE IF NOT EXISTS departments ( id BIGSERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL, description TEXT, parent_id BIGINT );
CREATE TABLE IF NOT EXISTS users ( id UUID PRIMARY KEY, department_id BIGINT REFERENCES departments(id) NOT NULL, login VARCHAR ( 255 ) UNIQUE NOT NULL, email VARCHAR ( 255 ) UNIQUE NOT NULL, password VARCHAR ( 255 ) NOT NULL, first_name VARCHAR ( 100 ) NOT NULL, last_name VARCHAR ( 100 ) NOT NULL, patronymic VARCHAR ( 100 ), position VARCHAR ( 255 ) NOT NULL );
CREATE TABLE IF NOT EXISTS roles ( id BIGSERIAL PRIMARY KEY, name VARCHAR ( 100 ) UNIQUE NOT NULL );
CREATE TABLE IF NOT EXISTS user_roles ( user_id UUID REFERENCES users ( id ) ON DELETE CASCADE NOT NULL, role_id BIGSERIAL REFERENCES roles ( id ) ON DELETE CASCADE NOT NULL );
